package com.memtrip.eos_evm.eos.evm.contracts.misc

import com.memtrip.eos.chain.actions.ChainResponse
import com.memtrip.eos.core.crypto.EosPrivateKey
import com.memtrip.eos.http.rpc.model.transaction.response.TransactionCommitted
import com.memtrip.eos_evm.eos.evm.EvmContract
import com.memtrip.eos_evm.eos.evm.EvmSender
import com.memtrip.eos_evm.ethereum.EthAccount
import io.reactivex.Single
import org.web3j.abi.TypeReference
import org.web3j.abi.datatypes.Uint
import java.math.BigInteger

class ContractTransferContract(
    contractAccountName: String,
    contractPrivateKey: EosPrivateKey,
    contractEthAccount: EthAccount
) : EvmContract(
    contractAccountName,
    contractPrivateKey,
    contractEthAccount,
    ""
) {

    fun createContract(value: BigInteger): Single<ChainResponse<TransactionCommitted>> {
        return create(listOf(), value)
    }

    fun transfer(sender: EvmSender): Single<ChainResponse<TransactionCommitted>> {
        return executeMethod(
            "transfer",
            listOf(),
            listOf(),
            sender
        )
    }

    fun child2Balance(sender: EvmSender): Single<ChainResponse<TransactionCommitted>> {
        return executeMethod(
            "child2Balance",
            listOf(),
            listOf(object : TypeReference<Uint>() { }),
            sender
        )
    }
}