package com.memtrip.eos_evm.eos.contracts.ethereum_org

import com.memtrip.eos.http.rpc.Api
import com.memtrip.eos_evm.eos.AccountIdentifier
import com.memtrip.eos_evm.eos.Config
import com.memtrip.eos_evm.eos.SetupTransactions
import com.memtrip.eos_evm.eos.evm.contracts.ethereum_org.DaoLiquidDemocracyContract
import com.memtrip.eos_evm.eos.evm.contracts.ethereum_org.DaoMultiSigContract
import com.memtrip.eos_evm.eos.state.GetCode
import com.memtrip.eos_evm.ethereum.EthAsset
import com.memtrip.eos_evm.ethereum.pad256
import com.memtrip.eos_evm.ethereum.toHexString
import okhttp3.OkHttpClient
import okhttp3.logging.HttpLoggingInterceptor
import org.junit.Assert
import org.junit.Assert.assertEquals
import org.junit.Assert.fail
import org.junit.Test
import java.util.concurrent.TimeUnit

class DaoMultiSigContractTest {

    private val okHttpClient = OkHttpClient.Builder()
        .addInterceptor(HttpLoggingInterceptor().setLevel(HttpLoggingInterceptor.Level.BODY))
        .connectTimeout(10, TimeUnit.SECONDS)
        .readTimeout(10, TimeUnit.SECONDS)
        .writeTimeout(10, TimeUnit.SECONDS)
        .build()

    private val chainApi = Api(Config.CHAIN_API_BASE_URL, okHttpClient).chain

    private val setupTransactions = SetupTransactions(chainApi)

    private val getCode = GetCode(chainApi)

    @Test
    fun `The DaoMultiSig contract is created`() {

        // given
        val (contractAccountName, contractAccountPrivateKey, contractEthAccount) = setupTransactions.seedWithEvmBalance(30000)
        val contract = DaoMultiSigContract(contractAccountName, contractAccountPrivateKey, contractEthAccount)

        val (member1AccountName, member1PrivateKKey, member1EthAccount) = setupTransactions.seedWithEvmBalance()
        val member1AccountIdentifier = AccountIdentifier.create(member1AccountName, member1EthAccount.address)
        val (member2AccountName, member2PrivateKKey, member2EthAccount) = setupTransactions.seedWithEvmBalance()
        val member2AccountIdentifier = AccountIdentifier.create(member2AccountName, member2EthAccount.address)
        val (member3AccountName, member3PrivateKKey, member3EthAccount) = setupTransactions.seedWithEvmBalance()
        val member3AccountIdentifier = AccountIdentifier.create(member3AccountName, member3EthAccount.address)

        // when
        val createContractResponse = contract.createContract(
            member1AccountIdentifier.toHexString(),
            listOf(member2AccountIdentifier.toHexString(), member3AccountIdentifier.toHexString()),
            75,
            EthAsset.milliether(300)
        ).blockingGet()

        // then
        assertEquals(202, createContractResponse.statusCode)

        // and when
        val getCodeResult = getCode.getAll(
            contract.accountIdentifier.pad256().toHexString()
        ).blockingGet()

        if (getCodeResult !is GetCode.Record.Multiple) fail("code record not found") else {
            assertEquals(1, getCodeResult.items.size)
            assertEquals(
                "",
                getCodeResult.items[0].code
            )
            assertEquals(getCodeResult.items[0].address, contract.accountIdentifier.pad256().toHexString())
        }
    }
}